mapper.update:
update secondhand_trade_like
set likeflag = 'N'
where emp_no = #{empNo}
and trade_no = #{tradeNo}

update secondhand_trade_like
set likeflag = 'Y'
where emp_no = #{empNo}
and trade_no = #{tradeNo}

update secondhand_trade_like 
set likeFlag='N'
where board_no=#{boardNo} and emp_no=#{empNo}

update secondhand_trade_like 
set likeFlag='Y'
where board_no=#{boardNo} and emp_no=#{empNo}
